tasks.code:
with Ada.Text_IO; use Ada.Text_IO;
procedure Main is
  F : File_Type;
  Line : String (1..200);
  Last : Natural;
begin
  Open (F, In_File, "README.md");
  Get_Line (F, Line, Last);
  Put_Line (Line(1..Last));
  Close (F);
end Main;
